import React from 'react'
import { useState } from 'react'


const Contact = () => {

  const [data,setData]  = useState({
   fullname:"",
   phone:"",
   email :"",
    msg:"",


  });


    const InputEvent = (event) => {
       
       const {name,value}  =event.target;

              setData((preVal) => {
  
                 return  {
                         ...preVal,
                         [name] : value,


                 };

              });


    };


    const formSubmit = (e) => {
    
          e.preventDefault();
          alert('My name is ${data.fullname}.  My mobile number is ${data.phone }, and my email is ${data.email}, here i want to say ${data.msg}' );


    }



  return (
    <>
    
    <div className='my-5'>
   <h1 className='text center'>
     <center>Contact us  </center> 

   </h1>
    </div>
<div className='container_contact_div'>
  <div className='row'>
    <div className='col-md-6 col-10 mx-auto'>
      <forms onSubmit={formSubmit}>
      <div class="mb-3">
  <label for="exampleFormControlInput1" class="form-label">FullName</label>
  <input type="text" class="form-control" id="exampleFormControlInput1"
  name=""
  value={data.fullname}
  onChange={InputEvent}
  placeholder="Enter Your Name"         />
</div>
<div class="mb-3">

 
</div>

<div class="mb-3">
  <label for="exampleFormControlInput1" class="form-label">Phone Number</label>
  <input type="number" class="form-control" id="exampleFormControlInput1"
    name=""
    value={data.phone}
    onChange={InputEvent}
  placeholder="Contact number"/  >
</div>
<div class="mb-3">

</div>

<div class="mb-3">
  <label for="exampleFormControlInput1" class="form-label">Email address</label>
  <input type="email" class="form-control" id="exampleFormControlInput1"   name=""
  value={data.email}
  onChange={InputEvent} placeholder="name@example.com"/>

  
</div>
<div class="mb-3">
  <label for="exampleFormControlTextarea1" class="form-label">Any Query For me</label>
  <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"
    name=""
    value={data.msg}
    onChange={InputEvent}
  ></textarea>
</div>


<div class="col-12">
    <button class="btn btn-outline-primary" type="submit">Submit form</button>
  </div>

      </forms>


    </div>
  </div>
  </div>    
    
    
    
    
    
    
    </>
  )
}

export default Contact
